import numpy as np
import math

def rayleigh(wm=0,t=0,M=1,N=32):
    h=0
    No=N//4
    h=[0]*M#h為一個list  h[0]代表第0條路徑，h[1]代表第1條路徑....
    for k in range(M):
        for n in range(No):
            a = 2 * math.pi * n / N + 2 * math.pi * k / (M * N) + math.pi / (2 * M * N)
            h[k] += (1/math.sqrt(No))*(math.cos(wm*t*math.cos(a)) + 1j*math.sin(wm*t*math.sin(a)))
    return h